import { Injectable } from '@angular/core';
import * as signalR from '@aspnet/signalr';
import { MessageSendRequest } from './core/models/message.model';

@Injectable({
  providedIn: 'root',
})
export class SignalrService {
  constructor() {}

  hubConnection!: signalR.HubConnection;

token: string = "eyJhbGciOiJodHRwOi8vd3d3LnczLm9yZy8yMDAxLzA0L3htbGRzaWctbW9yZSNobWFjLXNoYTI1NiIsInR5cCI6IkpXVCJ9..tu7vmA6KK3yR6c2yC285ttWrHEIPIrqqZC6M5k_ntCs"

  startConnection = () => {
    this.hubConnection = new signalR.HubConnectionBuilder()
      .withUrl('http://localhost:5001/chatHub',  {
        accessTokenFactory: () => this.token,
        
        skipNegotiation: true,
        transport: signalR.HttpTransportType.WebSockets,
      })
      .build();

    this.hubConnection
      .start()
      .then(() => {
        console.log('Hub connection started!');
      })
      .catch((err) => console.log(`Error while starting connectionL ` + err));
  };

askServer(message: MessageSendRequest) {
    this.hubConnection
      .invoke('Send', message)
      .catch((err) => console.log(err));
  }
  askServerListener() {
    this.hubConnection.on("MessageEditResponse", (sometext) => {
        console.log(sometext);
    })
  }
}
